CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_GenerarDocumento`(
ParmIdVenta              int(11),
ParmTipoDocumento        char(1), -- 0: BOleta de pago / 1: Factura
ParmIdFormaPago          int(11), -- Forma en la que realizo el pago
ParmRuc                  char(11), -- RUC De los que son Factura
ParmRazonSocial          varchar(200) -- Razon Social de los que son Factura
)
BEGIN
-- Declarando Variables
declare NewNroSerieNum int default 0;
declare NewCodDocumentoNum int default 0;
Declare NewNroSerie  char(5);
Declare NewCodDocumento char(8);
declare VarRUC char(11) default '';
declare VarCodCliente int;
Declare FechaPago date default null; 
/*
DECLARE exit handler for sqlexception
  BEGIN
    select '0' as Resultado;
  ROLLBACK;
END;

DECLARE exit handler for sqlwarning
 BEGIN
    select '2' as Resultado;
 ROLLBACK;
END;
*/

START TRANSACTION;

-- Quitando espacios a parametros de caracter
Select Cast(iFnull(Max(NroSerie),'00001') as unsigned), cast(iFnull(Max(CodDocumento),'00000000') as unsigned)  into NewNroSerieNum , NewCodDocumentoNum 
from documentopago
where TipoDocumento = ParmTipoDocumento; 

set NewCodDocumentoNum = NewCodDocumentoNum + 1;
select cast(NewNroSerieNum as char(5)) into NewNroSerie;
select cast(NewCodDocumentoNum as char(8)) into NewCodDocumento;


select right(concat('0000000',NewNroSerie),5) into NewNroSerie;
select right(concat('0000000',NewCodDocumento),8) into NewCodDocumento;



If ParmTipoDocumento = '1' then -- Factura
	set  VarRUC = ParmRuc;
else  -- Boleta
	select IdCliente into VarCodCliente from venta where idventa = 1 ;
	select c.NrodocumentoIdentidad, Ltrim(Rtrim(concat(Nombres,' ',ApellidoPaterno,' ',ApellidoMaterno))) into VarRUC,ParmRazonSocial from Cliente c	
	where c.idcliente = VarCodCliente;
	
End If;

If ParmIdFormaPago = 3 then
	set FechaPago = curdate();
End If;


 INSERT INTO `ginobigionibd`.`documentopago`
(`TipoDocumento`,`NroSerie`,`CodDocumento`,`NroRuc`,`RazonSocial`,`FechaPago`,`UsuarioGenero`,`FehaGeneracion`,`UsuarioModifico`,`FechaModificacion`,`Venta_idVenta`)
VALUES
(ParmTipoDocumento,NewNroSerie,NewCodDocumento,VarRUC,ParmRazonSocial,FechaPago,'System',curdate(),'',null,ParmIdVenta);


/*
CALL `ginobigionibd`.`SP_GenerarDocumento`(1, 1, 2);
*/

COMMIT;

END ;;
