set ParmCodUbigeoCosto = Ltrim(rtrim(ParmCodUbigeoCosto));
set ParmCodTrxTarjeta = ltrim(rtrim(ParmCodTrxTarjeta));

-- Valores de Tipo de envío

If TipoMantenimiento = 'I' then
	-- Creando Codigo de Venta
  
    
	Select  ifnull(MAx(IdVenta),0) into NewVenta from Venta;	
    Set NewVenta = NewVenta + 1;
    set VarEstadoVenta = 5; -- Estado Generado
	If ParmTipoRecojo = '0' then -- Recojo en Almacen
			set ParmCodUbigeoCosto = '000000';
SELECT 
    COUNT(1)
INTO CountUbigeoAux FROM
    costoubigeo
WHERE
    CodUbigeoCosto = '000000';
            If CountUbigeoAux = 0 then
				Insert into costoubigeo values ('000000','NO UBIGEO','NO UBIGEO','NO UBIGEO',0.0,'1');
				-- Delete from costoubigeo where CodUbigeoCosto = '000000';
            End If;
            -- Costo de  Ubigeo por Recojo en almacen
            set VarMontoEntrega = 0;
			Set ParmDireccionEntrega = 'Recojo en Almacen Principal de GinoBigioni';
            
	End if;			
    
    
	If ParmTipoRecojo = '1' then -- Envio a Domicilio
	-- Costo de  Ubigeo por envío a domicilio
		Select CostoEnvio into VarMontoEntrega from CostoUbigeo  where CodUbigeoCosto = ParmCodUbigeoCosto;
	End if;	
    
    set VarTotalVenta = ParmTotal - VarMontoEntrega;
    set VarIGV = round(0.18 * VarTotalVenta,2);
    set VarSubTotal = Round(VarTotalVenta - VarIGV,2);
    
    /*
    select * from EstadoVenta
    select * from fomapago
    */
    
    If ParmIdFomaPago = 1 then-- Contado/Efectivo
			set VarEstadoVenta = 3; -- Pagado
            set ParmIdBanco = NULL;
            set ParmNroCuenta = '';
            set ParmNroCuotas = 0;
    End If;
    
    If ParmIdFomaPago = 2 then-- Consignación
			set VarEstadoVenta = 1; -- Pendiente Pago
			set ParmIdBanco = NULL;
			set ParmNroCuenta = '';
    End If;
    
    If ParmIdFomaPago = 3 then-- Tarjeta
			 set VarEstadoVenta = 3; -- Pagado
			 set ParmIdBanco = NULL;    
			 set ParmNroCuenta = '';
             set ParmNroCuotas = 0;
    End If;    
    
    If ParmIdFomaPago = 4 then-- Transferencia Bancaria
    set VarEstadoVenta = 2; -- Por confirmar
    set ParmNroCuotas = 0;
    End If;  
    
            INSERT INTO `ginobigionibd`.`venta`
(`IdVenta`,`IdCliente`,`FechaVenta`,`SubTotal`,`IGV`,`Descuento`,`Total`,`MontoEntrega`,`TotalVenta`,`NroCuotas`,`TipoRecojo`,
`UsuarioModifico`,`FechaModifico`,`UsuarioAnulo`,`FechaAnulo`,`NumOperaBancaria`,`FechaOperaBancaria`,`CodTrxTarjeta`,
`FechTrxTarjeta`,`Estado`,`CodUbigeoCosto`,`IdFomaPago`,`IdCuponDescuento`,`DireccionEntrega`,`IdEstadoVenta`,`IdBancos`,`NroCuenta`)
VALUES
(NewVenta,ParmIdCliente,VarFechaVenta,VarSubTotal,VarIGV,0.0,VarTotalVenta,VarMontoEntrega,ParmTotal,ParmNroCuotas,parmTipoRecojo,
'',null,'',null,'',null,null,
null,VarEstadoVenta,ParmCodUbigeoCosto,ParmIdFomaPago,null,ParmDireccionEntrega,VarEstadoVenta,ParmIdBanco,ParmNroCuenta
);    
    
    
If  ParmIdFomaPago = 2 then

	-- Generando Orden de Consignacion
		select ifnull(max(IdOrdenConsignacion),0) into NewOrdenConsignacion from ordenconsignacion;
        set NewOrdenConsignacion = NewOrdenConsignacion + 1 ;
        
	-- Generando Cronograma de Pago
    If ParmNroCuotas <= 0 then
		set ParmNroCuotas = 1;
    End If;
    
    set VarMontoxCuota = ParmTotal / ParmNroCuotas;
        
    If ParmNroCuotas = 1 then 
			set VarFechaAux = date_add(curdate(), interval 7 day);
			
            INSERT INTO `ginobigionibd`.`ordenconsignacion`
			(`IdOrdenConsignacion`,`FechaProgramada`,`IdVenta`,`NroCuota`,`MontoCuota`,`Mora`,`Estado`)
			VALUES
			(NewOrdenConsignacion,VarFechaAux,NewVenta,1,VarMontoxCuota,0.0,1);
    else
		my_loop: LOOP -- Inicio Bucle
			SET Varcounter=Varcounter+1;
            set VarFechaAux = date_add(VarFechaAux, interval 7 day);
            
			If ParmNroCuotas = Varcounter then -- La Ultima Cuota debe contener la diferencia a pagar
				set VarMontoxCuota = ParmTotal - VarAuxSumCuota;
			End if;
            
            INSERT INTO `ginobigionibd`.`ordenconsignacion`
			(`IdOrdenConsignacion`,`FechaProgramada`,`IdVenta`,`NroCuota`,`MontoCuota`,`Mora`,`Estado`)
			VALUES
			(NewOrdenConsignacion,VarFechaAux,NewVenta,Varcounter,VarMontoxCuota,0.0,1);  
            
			set VarAuxSumCuota = VarAuxSumCuota  + VarMontoxCuota ;   
			    
			If Varcounter = ParmNroCuotas then
					 LEAVE my_loop;
			End IF;
		
		END LOOP my_loop;-- Fin Bucle
        
		UPDATE cliente 
		SET     SaldoLineaCredito = SaldoLineaCredito - ParmTotal
		WHERE     idCliente = ParmIdCliente ;
		
              
    End If; 
End If;   

CALL `ginobigionibd`.`SP_GenerarDocumento`(NewVenta,ParmTipoDocumento,ParmIdFomaPago,ParmRuc,ParmRazonSocial);
select NewVenta as Resultado;  
   
End If;

