CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_PedidosDetalle`(                                                
ParmIdProducto          int(11),                                                  
ParmIdColorProducto 	int (11),
ParmNroTalla            int(11),                                                  
ParmCantidad            int(11),                                                  
ParmIdTipoUsuario		int(11)
)
BEGIN
-- Declarando Variables
Declare VarTotal double default 0.0;
declare VarSubTotal double default 0;
declare VarIdVenta int default 0 ;
declare VarPrecioUnitario double default 0.0;



DECLARE exit handler for sqlexception
  BEGIN
   select '1' as Resultado;
  ROLLBACK;
END;

DECLARE exit handler for sqlwarning
 BEGIN
    select '2' as Resultado;
 ROLLBACK;
END;
START TRANSACTION;


-- Recuperando la ultima Venta

select max(idventa) into VarIdVenta from venta;

-- Quitando espacios a parametros de caracter

If ParmIdTipoUsuario = 1 then
	select PrecioUnitario into VarPrecioUnitario from producto where IdProducto = ParmIdProducto;
else
	If ParmIdTipoUsuario = 2 then
    select PrecioVendedor into VarPrecioUnitario from producto where IdProducto = ParmIdProducto;
	End If;
End If;    


set VarTotal = ParmCantidad * VarPrecioUnitario;
set VarSubTotal = Round(VarTotal * 0.82,2);


-- Genernado el detalle de la venta
INSERT INTO `ginobigionibd`.`detalleventa`
(`IdVenta`,`IdProducto`,`IdColorProducto`,`NroTalla`,`Cantidad`,`PrecioUnitario`,`SubTotal`,`Total`)
VALUES
(VarIdVenta,ParmIdProducto,ParmIdColorProducto,ParmNroTalla,ParmCantidad,VarPrecioUnitario,VarSubTotal,VarTotal);

-- Quitando stock del almacen

Update productotallacolor
set StockVenta = StockVenta - ParmCantidad
where IdProducto = ParmIdProducto and IdColorProducto = ParmIdColorProducto and NroTalla = ParmNroTalla ;

select '1' as Resultado;

